import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import Pagination from "../Pagination";
// Removed FaE as it wasn't used
import { FaEdit, FaTrash, FaImage } from "react-icons/fa";
import { IoMdCloseCircle } from "react-icons/io";
import { PropagateLoader } from "react-spinners";
import { overrideStyle } from "../../utils/utils";
import {
  categoryAdd,
  messageClear,
  get_category,
} from "../../store/Reducers/categoryReducer";
import { useDispatch, useSelector } from "react-redux";
import toast from "react-hot-toast";
import Search from "../components/Search"; // Corrected path assumption

const Category = () => {
  const dispatch = useDispatch();
  // It's still good practice to ensure the initial state in the reducer has categorys: []
  // But this component logic will now handle undefined/null during loading
  const { loader, successMessage, errorMessage, categorys } = useSelector(
    (state) => state.category || {} // Keep the fallback for the slice itself
  );

  const [currentPage, setCurrentPage] = useState(1);
  const [searchValue, setSearchValue] = useState("");
  const [parPage, setParPage] = useState(5);
  const [show, setShow] = useState(false);
  const [imageShow, setImage] = useState(""); // Renamed state variable for clarity

  const [state, setState] = useState({
    name: "",
    image: "",
  });

  const imageHandle = (e) => {
    let files = e.target.files;
    if (files.length > 0) {
      setImage(URL.createObjectURL(files[0]));
      setState({
        ...state,
        image: files[0],
      });
    }
  };

  const add_category = (e) => {
    e.preventDefault();
    // Consider creating FormData when sending files
    const formData = new FormData();
    formData.append("name", state.name);
    formData.append("image", state.image); // Ensure backend handles 'image' field
    dispatch(categoryAdd(formData)); // Dispatch FormData
    // console.log(state) // Keep for debugging if needed
  };

  useEffect(() => {
    if (successMessage) {
      toast.success(successMessage);
      dispatch(messageClear());
      setState({
        // Reset form state
        name: "",
        image: "",
      });
      setImage(""); // Reset image preview
      setShow(false); // Close the form sidebar on success
    }
    if (errorMessage) {
      toast.error(errorMessage);
      dispatch(messageClear());
    }
  }, [successMessage, errorMessage, dispatch]); // Added dispatch to dependency array

  useEffect(() => {
    const obj = {
      parPage: parseInt(parPage),
      page: parseInt(currentPage),
      searchValue,
    };
    dispatch(get_category(obj));
  }, [searchValue, currentPage, parPage, dispatch]); // Added dispatch to dependency array

  return (
    <div className="px-2 lg:px-7 pt-5">
      <div className="flex lg:hidden justify-between items-center mb-5 p-4 bg-[#6a5fdf] rounded-md">
        <h1 className="text-[#d0d2d6] font-semibold text-lg">Category</h1>
        <button
          onClick={() => setShow(true)}
          className="bg-red-500 shadow-lg hover:shadow-red-500/40 px-4 py-2 cursor-pointer text-white rounded-sm text-sm"
        >
          Add
        </button>
      </div>

      <div className="flex flex-wrap w-full">
        <div className="w-full lg:w-7/12">
          <div className="w-full p-4 bg-[#6a5fdf] rounded-md">
            <Search
              setParPage={setParPage}
              setSearchValue={setSearchValue}
              searchValue={searchValue}
            />

            <div className="relative overflow-x-auto mt-4">
              {" "}
              {/* Added mt-4 for spacing */}
              <table className="w-full text-sm text-left text-[#d0d2d6]">
                <thead className="text-sm text-[#d0d2d6] uppercase border-b border-slate-700">
                  <tr>
                    <th scope="col" className="py-3 px-4">
                      No
                    </th>
                    <th scope="col" className="py-3 px-4">
                      Image
                    </th>
                    <th scope="col" className="py-3 px-4">
                      Name
                    </th>
                    <th scope="col" className="py-3 px-4">
                      Action
                    </th>
                  </tr>
                </thead>

                {/* ==== CONDITIONAL RENDERING START ==== */}
                <tbody>
                  {loader ? ( // 1. Check if loading
                    <tr>
                      <td colSpan="4" className="py-4 px-4 text-center">
                        {" "}
                        {/* Ensure colSpan matches header columns */}
                        {/* You could put a spinner component here too */}
                        Loading Categories...
                      </td>
                    </tr>
                  ) : categorys && categorys.length > 0 ? ( // 2. Check if categorys exists and has items
                    categorys.map((d, i) => (
                      <tr key={d._id || i}>
                        {" "}
                        {/* Use a unique ID from data if available (like d._id) */}
                        <td
                          scope="row"
                          className="py-2 px-4 font-medium whitespace-nowrap"
                        >
                          {" "}
                          {/* Adjusted padding */}
                          {/* Calculate item number based on pagination */}
                          {(currentPage - 1) * parPage + i + 1}
                        </td>
                        <td
                          scope="row"
                          className="py-2 px-4 font-medium whitespace-nowrap"
                        >
                          <img
                            className="w-[45px] h-[45px] rounded" /* Added rounded */
                            // Make sure the image path is correct based on your backend setup
                            src={d.image} // Assuming d.image is the full URL or path from the API
                            alt={d.name || "Category Image"}
                            onError={(e) => {
                              e.target.onerror = null;
                              e.target.src = "placeholder.jpg";
                            }} // Basic fallback
                          />
                        </td>
                        <td
                          scope="row"
                          className="py-2 px-4 font-medium whitespace-nowrap"
                        >
                          {d.name}
                        </td>
                        <td
                          scope="row"
                          className="py-2 px-4 font-medium whitespace-nowrap"
                        >
                          <div className="flex justify-start items-center gap-4">
                            {/* Add actual links/onClick handlers for edit/delete */}
                            <Link
                              to={`/admin/dashboard/category/edit/${
                                d.slug || d._id
                              }`}
                              className="p-[6px] bg-yellow-500 rounded hover:shadow-lg hover:shadow-yellow-500/50 text-white"
                            >
                              {" "}
                              {/* Added text-white */}
                              <FaEdit />
                            </Link>
                            <button
                              onClick={() => console.log("Delete:", d._id)}
                              className="p-[6px] bg-red-500 rounded hover:shadow-lg hover:shadow-red-500/50 text-white"
                            >
                              {" "}
                              {/* Added text-white */}
                              <FaTrash />
                            </button>
                          </div>
                        </td>
                      </tr>
                    ))
                  ) : (
                    // 3. If not loading and no items
                    <tr>
                      <td colSpan="4" className="py-4 px-4 text-center">
                        {" "}
                        {/* Ensure colSpan matches header columns */}
                        No categories found.
                      </td>
                    </tr>
                  )}
                </tbody>
                {/* ==== CONDITIONAL RENDERING END ==== */}
              </table>
            </div>

            {/* Pagination - Only show if not loading and there are categories to paginate */}
            {!loader && categorys && categorys.length > 0 && (
              <div className="w-full flex justify-end mt-4 bottom-4 right-4">
                <Pagination
                  pageNumber={currentPage}
                  setPageNumber={setCurrentPage}
                  // Assuming totalItem comes from Redux state eventually
                  totalItem={50} // Replace 50 with actual total count from API/Redux state (e.g., totalCategory)
                  parPage={parPage}
                  showItem={3} // Or 5, depending on preference
                />
              </div>
            )}
          </div>
        </div>

        {/* Add Category Form Section */}
        <div
          // Corrected class logic for responsiveness and positioning
          className={`w-full lg:w-5/12 mt-6 lg:mt-0 lg:pl-4 ${
            show ? "block" : "hidden" // Simpler show/hide for mobile toggle
          } lg:block`} // Ensure it's always block on large screens
        >
          {/* The Add Category form part - wrap it for styling */}
          <div className="w-full p-4 bg-[#6a5fdf] rounded-md text-[#d0d2d6]">
            <div className="flex justify-between items-center mb-4">
              <h1 className="text-[#d0d2d6] font-semibold text-xl">
                Add New Category
              </h1>
              {/* Close button only needed for the mobile overlay scenario, which isn't fully implemented here */}
              {/* You might re-introduce the absolute positioning overlay logic if needed */}
            </div>

            <form onSubmit={add_category}>
              <div className="flex flex-col w-full gap-1 mb-3">
                <label htmlFor="name">Category Name</label>
                <input
                  value={state.name}
                  onChange={(e) => setState({ ...state, name: e.target.value })}
                  className="px-4 py-2 focus:border-indigo-500 outline-none bg-[#ffffff] border border-slate-700 rounded-md text-[#000000]"
                  type="text"
                  id="name"
                  name="category_name"
                  placeholder="Category Name"
                  required // Added required
                />
              </div>

              <div className="mb-4">
                {" "}
                {/* Added margin bottom */}
                <label
                  className="flex justify-center items-center flex-col h-[238px] cursor-pointer border border-dashed hover:border-red-500 w-full border-[#d0d2d6] bg-slate-800" // Added bg color
                  htmlFor="image"
                >
                  {imageShow ? (
                    <img
                      className="w-full h-full object-cover"
                      src={imageShow}
                      alt="Category Preview"
                    /> // Added alt and object-cover
                  ) : (
                    <>
                      <span className="text-4xl mb-2">
                        {" "}
                        {/* Made icon larger */}
                        <FaImage />
                      </span>
                      <span>Select Image</span>
                    </>
                  )}
                </label>
                <input
                  onChange={imageHandle}
                  className="hidden"
                  type="file"
                  name="image"
                  id="image"
                  accept="image/*" // Specify accepted file types
                />
              </div>

              <button
                disabled={loader} // Disable button while loading
                className="w-full px-4 py-2 text-white bg-red-500 rounded-md hover:bg-red-600 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-700 disabled:opacity-50 disabled:cursor-not-allowed" // Improved styling & disabled state
              >
                {loader ? (
                  <PropagateLoader
                    color="#fff"
                    cssOverride={overrideStyle}
                    size={10}
                  /> // Adjusted size
                ) : (
                  "Add Category"
                )}
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Category;
