import React, { forwardRef, useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { FixedSizeList as List } from "react-window";
import {
  confirm_payment_request,
  get_payment_request,
  messageClear,
} from "../../store/Reducers/paymentReducer";
import moment from "moment";
import toast from "react-hot-toast";

function handleOnWheel({ deltaY }) {
  console.log("handleOnWheel", deltaY);
}

const outerElementType = forwardRef((props, ref) => (
  <div ref={ref} onWheel={handleOnWheel} {...props} />
));

const PaymentRequest = () => {
  const dispatch = useDispatch();
  const { successMessage, errorMessage, pendingWithdraws, loader } =
    useSelector((state) => state.payment);
  const [paymentId, setPaymentId] = useState("");
  const [showSetupModal, setShowSetupModal] = useState(false);
  const [setupLink, setSetupLink] = useState("");
  const [setupRequirements, setSetupRequirements] = useState([]);
  const [accountStatus, setAccountStatus] = useState(null);

  useEffect(() => {
    dispatch(get_payment_request());
  }, []);

  const confirm_request = (id) => {
    setPaymentId(id);
    dispatch(confirm_payment_request(id));
  };

  useEffect(() => {
    if (successMessage) {
      toast.success(successMessage);
      dispatch(messageClear());
    }
    if (errorMessage) {
      try {
        const response = JSON.parse(errorMessage);
        if (response.message && response.accountLink) {
          setSetupLink(response.accountLink);
          setSetupRequirements(response.requirements || []);
          setAccountStatus(response.accountStatus);
          setShowSetupModal(true);
        } else {
          toast.error(response.message || errorMessage);
        }
      } catch (e) {
        toast.error(errorMessage);
      }
      dispatch(messageClear());
    }
  }, [successMessage, errorMessage]);

  const Row = ({ index, style }) => {
    return (
      <div style={style} className="flex text-sm text-white font-medium">
        <div className="w-[25%] p-2 whitespace-nowrap">{index + 1}</div>
        <div className="w-[25%] p-2 whitespace-nowrap">
          ${pendingWithdraws[index]?.amount}
        </div>
        <div className="w-[25%] p-2 whitespace-nowrap">
          <span className="py-[1px] px-[5px] bg-slate-300 text-blue-500 rounded-md text-sm">
            {pendingWithdraws[index]?.status}
          </span>
        </div>
        <div className="w-[25%] p-2 whitespace-nowrap">
          {moment(pendingWithdraws[index]?.createdAt).format("LL")}
        </div>
        <div className="w-[25%] p-2 whitespace-nowrap">
          <button
            disabled={loader}
            onClick={() => confirm_request(pendingWithdraws[index]?._id)}
            className="bg-indigo-500 shadow-lg hover:shadow-indigo-500/50 px-3 py-[2px cursor-pointer text-white rounded-sm text-sm]"
          >
            {loader && paymentId === pendingWithdraws[index]?._id
              ? "Loading..."
              : "Confirm"}
          </button>
        </div>
      </div>
    );
  };

  return (
    <div className="px-2 lg:px-7 pt-5">
      <div className="w-full p-4 bg-[#6a5fdf] rounded-md">
        <h2 className="text-xl font-medium pb-5 text-[#d0d2d6]">
          Withdrawal Request
        </h2>
        <div className="w-full">
          <div className="w-full overflow-x-auto">
            <div className="flex bg-[#a7a3de] uppercase text-xs font-bold min-w-[340px] rounded-md">
              <div className="w-[25%] p-2"> No </div>
              <div className="w-[25%] p-2"> Amount </div>
              <div className="w-[25%] p-2"> Status </div>
              <div className="w-[25%] p-2"> Date </div>
              <div className="w-[25%] p-2"> Action </div>
            </div>
            {
              <List
                style={{ minWidth: "340px" }}
                className="List"
                height={350}
                itemCount={pendingWithdraws.length}
                itemSize={35}
                outerElementType={outerElementType}
              >
                {Row}
              </List>
            }
          </div>
        </div>
      </div>

      {/* Stripe Account Setup Modal */}
      {showSetupModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg max-w-md w-full">
            <h3 className="text-xl font-semibold mb-4">Complete Stripe Account Setup</h3>
            <p className="text-gray-600 mb-4">
              The seller needs to complete their Stripe account setup before receiving payments.
            </p>
            
            {accountStatus && (
              <div className="mb-4">
                <h4 className="font-medium mb-2">Account Status:</h4>
                <ul className="list-disc pl-5">
                  <li className="text-gray-600">
                    Charges Enabled: {accountStatus.charges_enabled ? 'Yes' : 'No'}
                  </li>
                  <li className="text-gray-600">
                    Payouts Enabled: {accountStatus.payouts_enabled ? 'Yes' : 'No'}
                  </li>
                  <li className="text-gray-600">
                    Details Submitted: {accountStatus.details_submitted ? 'Yes' : 'No'}
                  </li>
                </ul>
              </div>
            )}

            {setupRequirements.length > 0 && (
              <div className="mb-4">
                <h4 className="font-medium mb-2">Required Information:</h4>
                <ul className="list-disc pl-5">
                  {setupRequirements.map((req, index) => (
                    <li key={index} className="text-gray-600">{req}</li>
                  ))}
                </ul>
              </div>
            )}

            <div className="flex justify-end gap-3">
              <button
                onClick={() => setShowSetupModal(false)}
                className="px-4 py-2 text-gray-600 hover:text-gray-800"
              >
                Close
              </button>
              <a
                href={setupLink}
                target="_blank"
                rel="noopener noreferrer"
                className="px-4 py-2 bg-indigo-500 text-white rounded hover:bg-indigo-600"
              >
                Complete Setup
              </a>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default PaymentRequest;
