import React from "react";
import { IoMdClose } from "react-icons/io";

const ChatSeller = () => {
  const [show, setShow] = React.useState(false);

  return (
    <div>
      <div className="px-2 lg:px-7 py-5 ">
        <div className="w-full bg-[#6a5fdf] px-4 py-4 rounded-md h-[calc(100vh-140px)] ">
          <div className="flex w-full h-full relative">
            <div className="flex w-full h-full relative">
              <div
                className={`flex w-[280px] h-full absolute z-10 ${
                  show ? "-left-[16px]" : "-[336px] "
                } md:left-0 md:relative transition-all `}
              >
                <div className="w-full h-[calc(100vh-177px)] bg-[#9e97e9] md:bg-transparent overflow-y-auto rounded-md ">
                  <div className="flex text-xl justify-between items-center p-4 md:p-0 md:px-3 pb-3 text-white">
                    <h2>Sellers</h2>
                    <span
                      onClick={() => setShow(true)}
                      className="block cursor-pointer md:hidden"
                    >
                      <IoMdClose />
                    </span>
                  </div>
                </div>
              </div>

              <div></div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ChatSeller;
